import React from 'react';
import CountUp from 'react-countup';

const stats = [
  { value: 354, suffix: '+', label: 'Completed Projects' },
  { value: 119, suffix: '+', label: 'Robotic Automation' },
  { value: 99, suffix: '%', label: 'Web Site Analyse' },
  { value: 321, suffix: '+', label: 'Clients Support Done' },
];

const StatsCounter = () => {
  return (
    <div className="w-full bg-gradient-to-r from-purple-500 to-blue-400 text-white py-12">
      <div className="max-w-6xl mx-auto flex flex-wrap justify-around gap-8 px-4">
        {stats.map((stat, index) => (
          <div key={index} className="text-center">
            <div className="text-4xl font-bold">
              <CountUp end={stat.value} suffix={stat.suffix} duration={2} />
            </div>
            <p className="mt-2 text-lg">{stat.label}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StatsCounter;
