import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Footer from './Footer';
import BlogSidebar from './BlogSidebar';
import blogData from '../data/blogData';

const Blog = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const postsPerPage = 4;

  // ✅ Handle search input from sidebar
  const handleSearch = (term) => {
    setSearchTerm(term);
    setCurrentPage(1); // reset to first page on new search
  };

  // ✅ Filter blog posts based on search term
  const filteredPosts = blogData.filter(post =>
    post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    post.content.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredPosts.length / postsPerPage);
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = filteredPosts.slice(indexOfFirstPost, indexOfLastPost);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    window.scrollTo(0, 0);
  };

  return (
    <>
      <div className="above-sp-blog"></div>

      <div className="container-blog">
        <div className='banner-blog'>
          <div className='content-div'>
            <h4>Our blog</h4>
            <h1>Blog Articles</h1>
            <div className='breadcrumb'>
              <a href="/" className="breadcrumb-link">Home&nbsp;&nbsp;</a> / <span>&nbsp;&nbsp;Our blog</span>
            </div>
          </div>
        </div>

        <div className='blogs-sec'>
          <div className='left-side'>
            {currentPosts.length > 0 ? currentPosts.map(post => (
              <div className="blog-card" key={post.id}>
                <img src={post.image} alt={post.title} />
                <p className="blog-category">{post.category}</p>
                <h3>{post.title}</h3>
                <p className="blog-content">{post.content.slice(0, 100)}...</p>
                <Link to={`/blog/${post.id}`}>
                  <button className="read-more">LEARN MORE</button>
                </Link>
              </div>
            )) : (
              <p>No posts found matching your search.</p>
            )}
          </div>

          <div className='right-side'>
            {/* ✅ Pass handleSearch to BlogSidebar */}
            <BlogSidebar onSearch={handleSearch} />
          </div>
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="pagination">
            {[...Array(totalPages)].map((_, idx) => (
              <button
                key={idx}
                onClick={() => handlePageChange(idx + 1)}
                className={currentPage === idx + 1 ? 'active' : ''}
              >
                {idx + 1}
              </button>
            ))}
          </div>
        )}
      </div>

      <Footer />
    </>
  );
};

export default Blog;
