import React from 'react';

function BlogSidebar({ searchTerm, setSearchTerm }) {
  return (
    <div className="sidebar">
      {/* Search Bar */}
      <div className="sidebar-section search-box">
        <input
          type="text"
          placeholder="Search"
          className="search-input"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button">SEARCH</button>
      </div>

      {/* You can make Latest Posts & Tags dynamic too if needed */}
      <div className="sidebar-section tags-box">
        <h3>Tags</h3>
        <div className="tags">
          {['Web', 'Agency', 'Company', 'Creative', 'Html', 'Marketing', 'Social Media', 'Branding'].map((tag, index) => (
            <span className="tag" key={index}>{tag}</span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default BlogSidebar;
