import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Footer from './Footer';
import BlogSidebar from './BlogSidebar';
import blogData from '../data/blogData';

function Blog() {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  const getExcerpt = (text, wordCount = 25) => {
    return text.split(' ').slice(0, wordCount).join(' ') + '...';
  };

  const filteredPosts = blogData.filter((post) =>
    post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    post.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
    post.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      <div className="above-sp-blog"></div>
      <div className="container-blog">
        <div className="banner-blog">
          <div className="content-div">
            <h4>Our blog</h4>
            <h1>Blog Articles</h1>
            <div className="breadcrumb">
              <a href="/" className="breadcrumb-link">Home {'\u00A0\u00A0'}</a> / <span>{'\u00A0\u00A0'}Our blog</span>
            </div>
          </div>
        </div>

        <div className="blogs-sec">
          <div className="left-side">
            {filteredPosts.length > 0 ? (
              filteredPosts.map((post) => (
                <div className="blog-card" key={post.id}>
                  <img src={post.image} alt={post.title} />
                  <p className="blog-category">
                    <img src="./edit-Icon.svg" alt="icon" /> {post.category}
                  </p>
                  <h3>{post.title}</h3>
                  <p className="blog-content">{getExcerpt(post.content)}</p>
                  <Link to={`/blog/${post.id}`}>
                    <button className="read-more">LEARN MORE</button>
                  </Link>
                </div>
              ))
            ) : (
              <p>No posts found for "{searchTerm}"</p>
            )}
          </div>

          <div className="right-side">
            <BlogSidebar onSearch={handleSearch} />
          </div>
        </div>
      </div>

      <Footer />
    </>
  );
}

export default Blog;
