    import React, { useEffect, useState } from 'react';
    import { useParams } from 'react-router-dom';
    import BlogSidebar from './BlogSidebar';
    import Footer from './Footer';

    const SingleBlogPost = () => {
    const { id } = useParams(); // assumes route like /blog/:id
    const [post, setPost] = useState(null);

    useEffect(() => {
        // Replace this with a real API call or data fetching logic
        const fakeBlogData = [
        {
            id: '1',
            title: 'Improve design with typography?',
            category: 'Creativity',
            content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Etiam nec.',
            image: './blog-1.png',
        },
        {
            id: '2',
            title: 'Interactivity connect consumer',
            category: 'Design',
            content: 'Ut enim ad minima veniam, quis nostrum exercitationem ullam.',
            image: './blog-2.png',
        },
        // Add more blog data as needed
        ];

        const foundPost = fakeBlogData.find(p => p.id === id);
        setPost(foundPost);
    }, [id]);

    if (!post) {
        return <div className="loading">Loading post...</div>;
    }

    return (
        <>
            <div className="above-sp-blog">
            
            </div>
            <div className='banner-blog'>
            <div className='content-div'>
                <h4>News details</h4>
                <h1>Blog Single</h1>
                <div className='breadcrumb'>
                <a href="/" className="breadcrumb-link">Home {'\u00A0\u00A0'}</a> / <span>{'\u00A0\u00A0'}News details</span>
                </div>
            </div>
            </div>

        <div className="single-blog-container">
            <div className="single-blog-left">
            <img src={post.image} alt={post.title} className="post-image" />
            <p className="blog-category"><img src="./edit-Icon.svg" alt="icon" /> {post.category}</p>
            <h1>{post.title}</h1>
            <p className="blog-content">{post.content}</p>
            </div>
            <div className="single-blog-right">
            <BlogSidebar />
            </div>
        </div>

        <Footer />
        </>
    );
    };

    export default SingleBlogPost;
