import React, { useState } from 'react';

function BlogSidebar({ onSearch }) {
  const [term, setTerm] = useState('');

  const handleInputChange = (e) => {
    setTerm(e.target.value);
  };

  const handleSearchClick = () => {
    if (typeof onSearch === 'function') {
      onSearch(term);
    }
  };
  

  return (
    <div className="sidebar">
      <div className="sidebar-section search-box">
        <input
          type="text"
          placeholder="Search"
          className="search-input"
          value={term}
          onChange={handleInputChange}
        />
        <button className="search-button" onClick={handleSearchClick}>
          SEARCH
        </button>
      </div>

      {/* Optional: Static Latest Posts & Tags - can be made dynamic too later */}
      <div className="sidebar-section latest-posts">
        <h3>Latest Posts</h3>
        <div className="post-item">
          <img src="./post-1.png" alt="post1" />
          <div>
            <p className="post-title">Thoughtful living in Los Angeles</p>
            <span className="post-date">03 Mar 2018</span>
          </div>
        </div>
        {/* Repeat for other posts or make dynamic if needed */}
      </div>

      <div className="sidebar-section tags-box">
        <h3>Tags</h3>
        <div className="tags">
          {['Web', 'Agency', 'Company', 'Creative', 'Html', 'Marketing', 'Social Media', 'Branding'].map((tag, index) => (
            <span className="tag" key={index}>{tag}</span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default BlogSidebar;
