import React, { useState } from 'react';

function BlogSidebar({ onSearch, blogData }) {
  const [term, setTerm] = useState('');

  const handleInputChange = (e) => {
    const newTerm = e.target.value;
    setTerm(newTerm);
    // Optional: uncomment below for real-time search
    // onSearch(newTerm);
  };

  const handleSearchClick = () => {
    if (typeof onSearch === 'function') {
      onSearch(term);
    }
  };

  // Get the latest 5 blog posts (assuming blogData is sorted by newest last)
  const latestPosts = blogData.slice(-5).reverse(); // reverse so newest on top

  return (
    <div className="sidebar">
      <div className="sidebar-section search-box">
        <input
          type="text"
          placeholder="Search"
          className="search-input"
          value={term}
          onChange={handleInputChange}
        />
        <button className="search-button" onClick={handleSearchClick}>
          SEARCH
        </button>
      </div>

      <div className="sidebar-section latest-posts">
        <h3>Latest Posts</h3>
        {latestPosts.map((post) => (
          <div className="post-item" key={post.id}>
            <img src={post.image} alt={post.title} />
            <div>
              <p className="post-title">{post.title}</p>
              <span className="post-category">{post.category}</span>
            </div>
          </div>
        ))}
      </div>

      <div className="sidebar-section tags-box">
        <h3>Tags</h3>
        <div className="tags">
          {['Web', 'Agency', 'Company', 'Creative', 'Html', 'Marketing', 'Social Media', 'Branding'].map((tag, index) => (
            <span className="tag" key={index}>{tag}</span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default BlogSidebar;
